ytd-app:not([use-content-visibility]) #page-manager.ytd-app {
  margin-top: 0;
}

ytd-masthead {
  opacity: 0;
  transition: all 300ms ease-in-out;
}

ytd-masthead:hover {
  opacity: 1;
}

body#stylus-options #options,
  #notes,
  body:not(#stylus-options):not(#stylus-popup) {
    background: #0d0d0d!important;
    color: #e6e6e6!important
  }
  #header {
    color: #fff!important;
    background: #0d0d0d!important;
    border-style: none!important
  }
  #stylus-popup {
    background: #1a1919!important;
    color: #fff!important
  }
  #hotkey-info[data-active] {
    border-left: 2px solid #000!important
  }
  #hotkey-info div {
    border-top: 1px solid #000!important;
    background-color: #000!important
  }
  #help-popup,
  #sections,
  .newUI .target,
  #message-box-contents {
    color: #e6e6e6!important
  }
  #help-popup,
  #message-box > div,
  #message-box-buttons,
  #message-box-title,
  body:not(#stylus-options):not(#stylus-popup) {
    background: #000!important
  }
  .search-result {
    background: #1a1919!important;
    border-color: #404040!important
  }
  .search-result:hover {
    border-color: #e6e6e6!important
  }
  .search-result-meta {
    background-color: #1a1919!important;
    color: #e6e6e6!important
  }
  button,
  input:not([type]),
  select {
    font-family: arial,sans-serif!important
  }
  input[type=checkbox]:not(.slider) {
    background: #0d0d0d!important
  }
  .firefox select,
  .sorter-selection select,
  input:not(.slider) {
    background-color: #0d0d0d!important;
    color: #fff!important;
    border: 1px solid #747477!important
  }
  .filter-selection select {
    background-color: transparent!important;
    border: none!important
  }
  #filters .filter-selection:hover {
    background-color: transparent!important
  }
  button {
    background: #000!important;
    border: 1px solid #484848!important;
    color: #fff!important;
    border-radius: 3px!important
  }
  .svg-icon.checked {
    fill: #45a1ff!important
  }
  button:hover {
    color: #45a1ff!important
  }
  a,
  a:visited {
    color: #e1e1e1!important
  }
  .dropdown-content {
    background: #000!important
  }
  .dropdown-content a:hover {
    background: #000!important;
    color: #45a1ff!important
  }
  #save-button:lang(en):after {
    content: 'd'!important
  }
  body.dirty #save-button:lang(en):after {
    content: '?'!important
  }
  .newUI .entry:hover .svg-icon:hover,
  .svg-icon:hover {
    fill: #45a1ff!important
  }
  #message-box.danger #message-box-title {
    background-color: #b22222!important
  }
  .newUI .can-update .update,
  .newUI .no-update:not(.update-problem):not(.update-done) .up-to-date {
    color: #000!important
  }
  .svg-icon {
    fill: #e6e6e6!important
  }
  #url {
    filter: invert(65%)!important
  }
  .CodeMirror-gutters {
    color: #e6e6e6!important
  }
  /* Manage: Table of Styles */
  .newUI .entry {
    background: #050505!important
  }
  .newUI .entry.odd {
  background: #0a0a0a!important
  }
  .newUI .entry:hover {
    background: #0d0d0d!important
  }
  .disabled h2::after,
  .entry.usercss .style-name-link::after {
    border: 1px solid #504e4e!important;
    color: #e1e1e1!important;
    background-color: #504e4e!important;
  }
  .svg-icon.select-arrow {
    fill: #fff!important
  }
  .newUI .entry .style-name:hover::before {
    background: 0 0!important
  }
  body.all-styles-hidden-by-filters::after,
  body.all-styles-hidden-by-filters::before {
    color: #45a1ff!important
  }
  /* Search Result Badge */
  .active #filters-stats {
    background-color: #004d99!important;
    border-color: #45a1ff!important
  }
  #reset-filters {
    fill: #45a1ff!important
  }
  .CodeMirror {
    border: 1px solid #1a1919;
    border-radius: 4px!important
  }
  .Codemirror-hints {
    background: #353b48!important;
    border-color: #404040!important
  }
  .Codemirror-hint {
    color: #d7d7db!important
  }
  .CodeMirror {
    background: #000!important;
    color: #e6e6e6!important
  }
  div.CodeMirror-selected {
    background: #353b48!important
  }
  .CodeMirror-line > span > span::selection,
  .CodeMirror-line > span::selection,
  .CodeMirror-line::selection {
    background: #353b48!important
  }
  .CodeMirror-line > span > span::-moz-selection,
  .CodeMirror-line > span::-moz-selection,
  .CodeMirror-line::-moz-selection {
    background: #353b48!important
  }
  .CodeMirror-gutters {
    background: #000!important;
    border-right: 0 solid #1a1919!important
  }
  .CodeMirror-guttermarker,
  .CodeMirror-guttermarker-subtle {
    color: #555!important
  }
  .CodeMirror-linenumber {
    color: #939393!important
  }
  .CodeMirror-cursor {
    border-left: 1px solid #fff!important
  }
  /* Comment Color #a50 */
  span.cm-comment {
    color: hsl(30, 100%, 67%)!important
  }
  span.cm-atom,
  span.cm-attribute,
  span.cm-builtin,
  span.cm-error,
  span.cm-keyword,
  span.cm-quote {
    color: #ff7de9!important
  }
  span.cm-number,
  span.cm-string,
  span.cm-string-2 {
    color: #6b89ff!important
  }
  span.cm-hr,
  span.cm-meta {
    color: #939393!important
  }
  span.cm-header,
  span.cm-qualifier,
  span.cm-variable-2 {
    color: #75bfff!important
  }
  span.cm-property {
    color: #86de74!important
  }
  span.cm-bracket,
  span.cm-def,
  span.cm-link:visited,
  span.cm-tag {
    color: #75bfff!important
  }
  span.cm-variable {
    color: #b98eff!important
  }
  span.cm-variable-3 {
    color: #d7d7db!important
  }
  span.cm-link {
    color: #404040!important
  }
  span.cm-operator {
    color: #e6e6e6!important
  }
  span.cm-special {
    color: #d7d7db!important
  }
  .CodeMirror-activeline-background {
    background: rgba(185,215,253,.15)!important
  }
  .CodeMirror-matchingbracket {
    outline: rgba(255,255,255,.25) solid 1px!important;
    color: #fff!important
  }
  select {
    color: #fff!important
  }
  optgroup,
  option {
    background-color: #000
  }
  #confirm > div {
    background-color: #1a1919
  }
  .menu-items-wrapper {
    background-color: #1a1919;
  }
/*  Original Options Title background-color: rgb(145, 208, 198); */
  #options-title {
    background: #2e6b61;
    color: #fff;
  }
  input[type="radio"]:checked:after {
    background-color: #d7d7db;
  }
  .onoffswitch span:before {
    background: #999 !important
  }
  .onoffswitch input + span {
    background-color: #555 !important;
  }
  /* Original #04BA9F */
  .onoffswitch input:checked + span::before{
    background: #038773 !important
  }
  /* Original #CAEBE3 */
  .onoffswitch input:checked + span {
    background-color: #81cfbc !important;
  }
  [disabled] {
    opacity: 50%;
  }
  button[disabled]:hover {
    color: unset !important;
  }
.colorpicker-popup {
    background-color: #222;
    border: 1px solid #737373;
}
.colorpicker-title,
.colorpicker-title-action[data-active],
.colorpicker-format-change-button,
.colorpicker-palette-hint,
.colorpicker-popup-mover{
    color: #fff;
}
.colorpicker-format-change-button:hover {
    color: #e6e6e6
}
/* .warnings #ffe2e2 */
.warnings {
    background-color: #700000
}

